import Vue from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)
//存放数据的地方
const state={
    count
}
//修改state的唯一手段
const mutations={}
//处理action，可以书写自己的业务逻辑，也可以处理自己的异步
const actions={}
//getters:理解为计算属性，用于简化仓库数据，让组件区数据更方便
const getters={}
export default new Vuex.Store({
    state,
    mutations,
    actions,
    getters
})
