import requests from "./request";
import mockRequest from './mockRequest'
import axios from "axios";

//三級聯動接口

export const reqCategoryList = ()=>
    //返回promise對象
   requests({url:'/product/getBaseCategoryList',method:'get'});
//获取轮播图banner
export const reqGetBannerList = ()=>mockRequest({url:'/banner',method:'get'});
//获取floor的数据
export const reqFloorList = ()=>mockRequest({url:'/floor',method:'get'});
//获取搜索商品的接口 地址api/lsit 请求方式post 需要带参数,至少是一个空对象
export const reqGetSearchInfo=(params)=>requests({url:'/list',method:'post',data:params})
export const reqGoodsInfo =(skuId)=>requests({url:`/item/${skuId}`,method:'get'});
//添加产品到购物车
// export const AddOrUpdateShopCart=(skuId,skuNum)=>({url:`/cart/addToCart/${skuId}/${skuNum}`,method:'post'})
export const reqAddOrUpdateShopCart = (skuId,skuNum)=>requests({url:`/cart/addToCart/${skuId}/${skuNum}`,method:"post"})
//获取购物车列表数据接口
//URL:/api/cart/cartList   method:get 
export const reqCartList = ()=>requests({url:'/cart/cartList',method:'get'});
//删除购物产品的接口
//URL:/api/cart/deleteCart/{skuId}   method:DELETE  
export const reqDeleteCartById = (skuId)=>requests({url:`/cart/deleteCart/${skuId}`,method:'delete'});
//修改商品的选中状态
//URL:/api/cart/checkCart/{skuId}/{isChecked}   method:get 
export const reqUpdateCheckedByid = (skuId,isChecked)=>requests({url:`/cart/checkCart/${skuId}/${isChecked}`,method:'get'});
