import requests from "./request";
import mockRequest from './mockRequest'
import axios from "axios";
//三級聯動接口

// export const reqCategoryList = ()=>
//     //返回promise對象
//    requests({url:'/product/getBaseCategoryList',method:'get'});
export const reqCategoryList = ()=>
    //返回promise對象
   requests({url:'https://mock.presstime.cn/mock/629df60b1a23490028bcaf5e/api/list '});
//获取轮播图banner
export const reqGetBannerList = ()=>mockRequest({url:'/banner',method:'get'});
//获取floor的数据
export const reqFloorList = ()=>mockRequest({url:'/floor',method:'get'});
//获取搜索商品的接口 地址api/lsit 请求方式post 需要带参数,至少是一个空对象
export const reqGetSearchInfo=(params)=>requests({url:'/list',method:'post',data:params})
export const reqGoodsInfo =(skuId)=>requests({url:`/item/${skuId}`,method:'get'});
