import {reqGoodsInfo} from "@/api"
//import {getUUID} from '@/utils/uuid_token'
const state={
    //state数据别乱写,服务器返回数据，返回数组【根据服务器返回的类型】
    goodInfo: {},
    //uuid_token:getUUID()
}
//修改state的唯一手段
const mutations={
    GETGOODINFO(state, goodInfo) {
        state.goodInfo = goodInfo;
      },
    ADDOR(state,skuNum){
      state.skuNum=skuNum
    },
  
    
}
//处理action，可以书写自己的业务逻辑，也可以处理自己的异步
const actions={
    //通过API里面的接口函数调用，向服务器发请求，获取服务器数据
    async getGoodInfo({ commit }, skuId) {
        let result = await reqGoodsInfo(skuId);
        if (result.code == 200) {
          commit("GETGOODINFO", result.data);
        }
      },
  //ADD产品到购物车
  
  async AddOrUpdateShopCart({commit},{skuId,skuNum}){
    let result= await addOrUpdateShopCart(skuId,skuNum)
    commit('ADDOR',result.data)
  }
};
//getters:理解为计算属性，用于简化仓库数据，让组件区数据更方便
const getters={
    categoryView(state) {
        //比如:state.goodInfo初始状态空对象，空对象的categoryView属性值undefined
        //当前计算出的 categoryView属性值至少是一个空对象，假的报错不会有了。
        return state.goodInfo.categoryView || {};
      },
      //简化产品信息的数据
      skuInfo(state) {
        return state.goodInfo.skuInfo || {};
      },
      spuSaleAttrList(state){
        return state.goodInfo.spuSaleAttrList||[];
      }
}
export default({
    state,
    mutations,
    actions,
    getters
})
