import {reqSearchInfo} from '@/api'
const state={}
//修改state的唯一手段
const mutations={}
//处理action，可以书写自己的业务逻辑，也可以处理自己的异步
const actions={
    //至少传入一个空对象
    async getSearchList({commit},params){
        await reqSearchInfo(params)
    }
}
//getters:理解为计算属性，用于简化仓库数据，让组件区数据更方便
const getters={}
export default({
    state,
    mutations,
    actions,
    getters
})
