import Home from '@/pages/Home'
import Search from '@/pages/Search'
import Login from '@/pages/Login'
import Register from '@/pages/Register'
import Detail from '@/pages/Detail'
export default[{ 
       
    name: 'detail',  // 是当前路由的标识名称
    path: '/detail/',
    component: Detail,
        },
        {
            path:'/home',
            component:Home,
            meta:{show:true}
        },
        {
            path:'/search',
            component:Search,
            name:'search',
            meta:{show:true}
        },
        {
            path:'/login',
            component:Login,
            meta:{show:false}
        },
        {
            path:'/register',
            component:Register,
            meta:{show:false}
        },
        //重定向，在项目跑起来的时候，访问/。立马让他定向到首页
     {
         path:'*',
         redirect:'/home'
     }
]
