//axios进行二次封装
import axios from 'axios';
//创建axios实例[创建出来的实例即为axios，只不过可以配置一些东西]
let instance = axios.create({
    //可以给请求路径中添加一些参数 
    baseURL: "/api",
    //设置请求时间（5S）
    timeout: 5000
});
