import {reqGetCode,reqUserRegister}from '@/api'
const state={}
const mutations={}
const actions={
    //获取验证码
   async getCode({commit},phone){
   let result=await reqGetCode(phone)
   if(result.code==200){
    commit
   }
   console.log(result)
    }
}
const getters={}
export default{
    state,
    mutations,
    actions,
    getters
}
